import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';

import { RootState } from '../Reducers';

import { Text, Box, Checkbox, Flex } from '@chakra-ui/react';
import { discountReducer } from '../Reducers/discountReducer';

export default function DiscountMenu(props: {name: string, quantity: number, excludedPrice: number}) {
  const {name, quantity, excludedPrice} = props;
  const { discounts } = useSelector((store: RootState) => store.discountReducer);
  const dispatch = useDispatch();
  const [isChecked, setIsChecked] = useState(true);

  const onSelect = (name: string): void => {
    dispatch(discountReducer.actions.SELECT_MENU(name));
  }

  function menuCheckboxToggleHandler(event: any) {
    onSelect(name);
    const checked = event.target.checked;
    if(!checked) {
      console.log("해제")
      setIsChecked(false);
      return;
    }
    setIsChecked(true); 
  }

  return (
    <Box key={name} marginY="20px">
      <Flex alignItems="center">
        <Checkbox id={name} isChecked={isChecked} onChange={menuCheckboxToggleHandler} defaultChecked> 
        <Box marginX="15px">
          <Text fontSize="lg" fontWeight="semibold">{name} X {quantity}</Text>
          <Text fontSize="sm" color="red">- ₩{excludedPrice}</Text>
        </Box>
        </Checkbox>
        </Flex>
    </Box>
  )
}
