import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';

import { RootState } from '../Reducers';
import { Discounts } from '../Interface/discountInterface';

import { Text, Box, Checkbox, Flex } from '@chakra-ui/react';
import { discountReducer } from '../Reducers/discountReducer';
import DiscountMenu from './DiscountMenu';

export default function DiscountsMenu(props: { id: string; name: string }) {
  const { discounts } = useSelector((store: RootState) => store.discountReducer);
  const dispatch = useDispatch();
  const [isChecked, setIsChecked] = useState(true);
  const { name } = props;
  const discountsArray = discounts.filter((discount: Discounts) => discount.name === name)[0];
  const discountedMenu = discountsArray.discountedMenu;

  const onSelect = (name: string): void => {
    dispatch(discountReducer.actions.SELECT_MENU(name));
  }

  function menuCheckboxToggleHandler(event: any) {
    onSelect(name);
    const checked = event.target.checked;
    if(!checked) {
      console.log("해제")
      setIsChecked(false);
      return;
    }
    setIsChecked(true); 
  }

  const $discountedList = discountedMenu.map((menu: any) => {
    const {name, quantity, _, excludedPrice } = menu;

    return (
      <DiscountMenu name={name} quantity={quantity} excludedPrice={excludedPrice} />
    // <Box key={name} marginY="20px">
    //   <Flex alignItems="center">
    //     <Checkbox id={name} isChecked={isChecked} onChange={menuCheckboxToggleHandler} defaultChecked> 
    //     <Box marginX="15px">
    //       <Text fontSize="lg" fontWeight="semibold">{name} X {quantitiy}</Text>
    //       <Text fontSize="sm" color="red">- ₩{excludedPrice}</Text>
    //     </Box>
    //     </Checkbox>
    //     </Flex>
    // </Box>
    );
  })

  return (
    <div>{$discountedList}</div>
  )  
}
