const initialState = {
  data: "",
  clickedFood: "",
  totalPrice: 0,
  isOverMinimum: false,
}
const LOGIN = "LOGINREDUCER/LOGIN";
const LOGOUT = "LOGINREDUCER/LOGOUT";

function cartReducer(state = visitor, action) {
  // 초기 상태로 visitor 지정
  switch (action.type) {
    case LOGIN:
      return {
        ...state,
        isLogined: true,
        userName: action.userName
      };
    case LOGOUT:
      return {
        isLogined: false,
        userName: ""
      }
    default:
      return state;
  }
  // default 대신 오류를 반환해야함.
}

export { cartReducer }
