import { createSlice, PayloadAction } from '@reduxjs/toolkit';

import { CartData } from './cartReducer';

export interface DiscountsData {
  discounts: Discounts[];
  totalPrice?: number;
}

export interface Discounts {
  id: string;
  name: string;
  discount_rate: number;
  discountedMenu?: DiscountedMenu[];
  discountedPrice?: number;
}

export interface DiscountedMenu {
  name: string;
  priceDividedByQuantitiy: number;
}

export const initialState = {
  discounts: [],
  totalPrice: 0,
}

function getDiscountedPrice(price: number, rate: number): number {
  return (100 - rate) * 0.01 * price;
}

export const discountReducer = createSlice({
  name: 'discountReducer',
  initialState: initialState as DiscountsData,
  reducers: {
    STORE: (state, { payload }: PayloadAction<Discounts[]>) => {
      state.discounts = payload;
    },
    UPDATE: (state, { payload }: PayloadAction<CartData>) => {
      state.discounts.forEach((discount) => {
        payload.storedFoods.forEach((food) => {
          const menuObject = {
            name: food.name,
            priceDividedByQuantitiy: getDiscountedPrice(food.price,discount.discount_rate)
          }
          discount.discountedMenu?.push()
        })

      })
    }
  }
});
